function parseDate(dateStr) {
  const months = {
    январь: 0,
    февраля: 1,
    февраль: 1,
    март: 2,
    марта: 2,
    апрель: 3,
    апреля: 3,
    май: 4,
    мая: 4,
    июнь: 5,
    июня: 5,
    июль: 6,
    июля: 6,
    август: 7,
    августа: 7,
    сентябрь: 8,
    сентября: 8,
    октябрь: 9,
    октября: 9,
    ноябрь: 10,
    ноября: 10,
    декабрь: 11,
    декабря: 11,
  };

  const [day, month, year] = dateStr.split(" ");
  return new Date(year, months[month.toLowerCase()], day);
}

export const newsData = news.sort((a, b) => parseDate(a.date) - parseDate(b.date));